import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsxRuntime classic */
/* @jsx mdx */
import { LayoutDocs } from 'components/LayoutDocs';

export const meta = {
  "title": "Text",
  "group": "svelteuidev-core",
  "packageGroup": "@svelteuidev/core",
  "category": "typography",
  "description": "Display text and links with theme styles",
  "example": "https://codesandbox.io/embed/text-s9fcj7?fontsize=14&hidenavigation=1&module=%2FApp.svelte&theme=dark&view=preview"
};

const layoutProps = {
  meta
};
const MDXLayout = ({ children, ...props }) => (
  <LayoutDocs meta={meta} {...props}>{children}</LayoutDocs>
)
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">



    <h2 {...{
      "id": "usage"
    }}>{`Usage`}<a parentName="h2" {...{
        "href": "#usage",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <p>{`Use Text component to display text and links with theme styles. Control Text styles with props:`}</p>
    <ul>
      <li parentName="ul"><strong parentName="li">{`size`}</strong>{` - font-size from `}<inlineCode parentName="li">{`theme.fontSizes`}</inlineCode>{` - xs, sm, md, lg, xl`}</li>
      <li parentName="ul"><strong parentName="li">{`color`}</strong>{` - color from `}<inlineCode parentName="li">{`theme.colors`}</inlineCode>{` - red, green, blue, etc.`}</li>
      <li parentName="ul"><strong parentName="li">{`weight`}</strong>{` - font-weight property - 500, 700, bold, semibold, etc.`}</li>
