import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsxRuntime classic */
/* @jsx mdx */
import { LayoutDocs } from 'components/LayoutDocs';

import { Alert } from '@mantine/core';
import { InfoCircledIcon } from '@radix-ui/react-icons'
export const meta = {
  "id": "introduction",
  "title": "Introduction"
};

const layoutProps = {
  meta
};
const MDXLayout = ({ children, ...props }) => (
  <LayoutDocs meta={meta} {...props}>{children}</LayoutDocs>
)
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">




    <p>{`Instead of remaking the same components, recreating custom actions, transitions, utilities, etc. SvelteUI is designed so you can start making projects quickly. It works amazingly well `}<strong parentName="p">{`out-of-the-box, with zero-configuration, and can be customized`}</strong>{` to your liking as your application grows.`}</p>
    <h3 {...{
      "id": "features"
    }}>{`Features`}<a parentName="h3" {...{
        "href": "#features",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h3>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://kit.svelte.dev/"
        }}>{`SvelteKit`}</a>{` and SSR Compatible`}</li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://typescriptlang.org/"
        }}>{`TypeScript`}</a>{` and type definitions are supported, but optional.`}</li>
      <li parentName="ul">{`All components are accessible according to `}<a parentName="li" {...{
          "href": "https://www.w3.org/WAI/standards-guidelines/aria/"
        }}>{`WAI-ARIA`}</a>{` standards.`}</li>
      <li parentName="ul">{`Dark theme included, as well as a custom theming api.`}</li>
      <li parentName="ul">{`Minimal third-party dependency usage.`}</li>
      <li parentName="ul">{`Easy setup - `}<strong parentName="li">{`Zero Configuration`}</strong></li>
    </ul>
    <h3 {...{
      "id": "undocumented-components"
    }}>{`Undocumented Components`}<a parentName="h3" {...{
        "href": "#undocumented-components",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h3>
    <p>{`The docs are still under construction. Some of the library's exported components & functions are still undocumented. `}<a parentName="p" {...{
        "href": "https://github.com/Brisklemonade/svelteui/issues/15"
      }}>{`Here`}</a>{` is where you can keep track of the progress.`}</p>
    <Alert icon={<InfoCircledIcon mdxType="InfoCircledIcon" />} title="Warning" color="yellow" mdxType="Alert">
Please keep in mind that any undocumented component could potentially receive breaking API changes or be heavily updated before being finalized.
    </Alert>
    <br />
    <h2 {...{
      "id": "packages"
    }}>{`Packages`}<a parentName="h2" {...{
        "href": "#packages",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <p><a parentName="p" {...{
        "href": "./core/button"
      }}><inlineCode parentName="a">{`@svelteui/core`}</inlineCode></a>{` - core components library`}</p>
    <p><a parentName="p" {...{
        "href": "./actions/use-click-outside"
      }}><inlineCode parentName="a">{`@svelteui/actions`}</inlineCode></a>{` - library of useful actions`}</p>
    <p><a parentName="p" {...{
        "href": "./motion/typewriter"
      }}><inlineCode parentName="a">{`@svelteui/motion`}</inlineCode></a>{` - collection of transitions to utilize on dom elements`}</p>
    <p><a parentName="p" {...{
        "href": "./utilities/os"
      }}><inlineCode parentName="a">{`@svelteui/utilities`}</inlineCode></a>{` - collection of helpful utility functions`}</p>
    <p><inlineCode parentName="p">{`@svelteui/prism`}</inlineCode>{` - code highlight component built with prism action (coming in v0.6.0 release)`}</p>
    <br />
    <h2 {...{
      "id": "getting-help"
    }}>{`Getting help`}<a parentName="h2" {...{
        "href": "#getting-help",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <p>{`I want to build a friendly community that is happy to help those who need it. Help build one up by joining the discord:`}</p>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://discord.gg/2J2xmzCS79"
        }}>{`Join Discord community`}</a>{` – it will be the easiest way to get help.`}</li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://github.com/Brisklemonade/svelteui/discussions"
        }}>{`Github Discussions`}</a>{` - you can discuss anything relating to the project here.`}</li>
    </ul>
    </MDXLayout>;
}

;
