import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsxRuntime classic */
/* @jsx mdx */
import { LayoutDocs } from 'components/LayoutDocs';

export const meta = {
  "title": "Title",
  "group": "svelteuidev-core",
  "packageGroup": "@svelteuidev/core",
  "category": "typography",
  "description": "Inline or block code without syntax highlight",
  "example": "https://codesandbox.io/embed/title-neubxl?fontsize=14&hidenavigation=1&module=%2FApp.svelte&theme=dark&view=preview"
};

const layoutProps = {
  meta
};
const MDXLayout = ({ children, ...props }) => (
  <LayoutDocs meta={meta} {...props}>{children}</LayoutDocs>
)
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">



    <h2 {...{
      "id": "usage"
    }}>{`Usage`}<a parentName="h2" {...{
        "href": "#usage",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <p>{`Use the Title component to render h1-h6 headings with SvelteUI theme styles.
By default, Title has no margins and paddings.`}</p>
    <p>{`Pass `}<inlineCode parentName="p">{`order`}</inlineCode>{` prop to render specific element (h1-h6), default order is 1:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-tsx"
      }}>{`<Title order={1}>This is h1 title</Title>
<Title order={2}>This is h2 title</Title>
<Title order={3}>This is h3 title</Title>
<Title order={4}>This is h4 title</Title>
<Title order={5}>This is h5 title</Title>
<Title order={6}>This is h6 title</Title>
`}</code></pre>
    <h2 {...{
      "id": "shared-props"
    }}>{`Shared Props`}<a parentName="h2" {...{
        "href": "#shared-props",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <p>{`The Title component takes all the same props as the Text component`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-tsx"
      }}>{`<Title order={1}>This is h1 title</Title>
<Title order={1} variant='gradient'>This is h1 title with a twist</Title>
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
