import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsxRuntime classic */
/* @jsx mdx */
import { LayoutDocs } from 'components/LayoutDocs';

export const meta = {
  "title": "Image",
  "group": "svelteuidev-core",
  "packageGroup": "@svelteuidev/core",
  "category": "data-display",
  "description": "Image with optional placeholder for loading and error state",
  "example": "https://codesandbox.io/embed/image-06mhdm?fontsize=14&hidenavigation=1&module=%2FApp.svelte&theme=dark&view=preview"
};

const layoutProps = {
  meta
};
const MDXLayout = ({ children, ...props }) => (
  <LayoutDocs meta={meta} {...props}>{children}</LayoutDocs>
)
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">



    <h2 {...{
      "id": "usage"
    }}>{`Usage`}<a parentName="h2" {...{
        "href": "#usage",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <p>{`Image component is a wrapper around img element with option to change object fit, radius and placeholder:`}</p>
    <h2 {...{
      "id": "width-and-height"
    }}>{`Width and height`}<a parentName="h2" {...{
        "href": "#width-and-height",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <p>{`In the example above, the image takes 100% of width of its container and height is calculated dynamically based on image proportion.
To change this behavior, set image width and height to define image size.`}</p>
    <p>{`Note that if image proportions do not match given width and height, some parts will be cut out.
In case you want to show image in its original proportions but fitted in current width and height set `}<inlineCode parentName="p">{`fit="contain"`}</inlineCode>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-tsx"
      }}>{`<Image
    width={200}
    height={80}
    src={url}
/>
<Image
    width={200}
    height={80}
    fit="contain"
    src={url}
/>
<Image
    height={80}
    src={url}
/>
`}</code></pre>
    <h2 {...{
      "id": "placeholder"
    }}>{`Placeholder`}<a parentName="h2" {...{
        "href": "#placeholder",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <p>{`By default, placeholders image placeholders are disabled.
Image placeholder is displayed in these cases:`}</p>
    <ul>
      <li parentName="ul"><inlineCode parentName="li">{`usePlaceholder`}</inlineCode>{` prop is set to true`}</li>
      <li parentName="ul">{`Image is currently loading`}</li>
      <li parentName="ul">{`Error occurred during image loading (e.g. broken link)`}</li>
      <li parentName="ul">{`Image did not receive `}<inlineCode parentName="li">{`src`}</inlineCode>{` prop`}</li>
    </ul>
    <p>{`Placeholder size is determined by width and height props.
Placeholder is centered vertically and horizontally across provided width and height.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-tsx"
      }}>{`<Image
    width={200}
    height={120}
    src={null}
    alt="Without placeholder"
/>
<Image
    width={200}
    height={120}
    src={null}
    alt="With default placeholder"
    usePlaceholder
/>
`}</code></pre>
    <h2 {...{
      "id": "with-caption"
    }}>{`With caption`}<a parentName="h2" {...{
        "href": "#with-caption",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <p>{`You can add figcaption to an image with `}<inlineCode parentName="p">{`caption`}</inlineCode>{` prop:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-tsx"
      }}>{`<div style='width: 240px; margin-left: auto; margin-right: auto;'>
  <Image
    radius='md'
    src='https://images.unsplash.com/photo-1627552245715-77d79bbf6fe2?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=640&q=80'
    alt='Random unsplash image'
    caption='My dog begging for treats'
  />
</div>
`}</code></pre>
    <h2 {...{
      "id": "radius"
    }}>{`Radius`}<a parentName="h2" {...{
        "href": "#radius",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <p>{`Radius is applied both to image and placeholder. Radius has predefined values: `}<inlineCode parentName="p">{`xs`}</inlineCode>{`, `}<inlineCode parentName="p">{`sm`}</inlineCode>{`, `}<inlineCode parentName="p">{`md`}</inlineCode>{`, `}<inlineCode parentName="p">{`lg`}</inlineCode>{`, `}<inlineCode parentName="p">{`xl`}</inlineCode>{`. Alternatively, you can use a number to set border-radius in px:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-tsx"
      }}>{`<Image radius={0} /> // -> default – image has no radius
<Image radius="lg" /> // -> theme predefined large radius
<Image radius={10} /> // -> { borderRadius: 10 }
`}</code></pre>
    <h2 {...{
      "id": "backgroundimage-component"
    }}>{`BackgroundImage component`}<a parentName="h2" {...{
        "href": "#backgroundimage-component",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <p>{`Use BackgroundImage component when you need to display image below any content.
Component sets background-image to given `}<inlineCode parentName="p">{`src`}</inlineCode>{`, background-size to `}<inlineCode parentName="p">{`cover`}</inlineCode>{` and background-position to `}<inlineCode parentName="p">{`center`}</inlineCode>{`.
It can be used for cards, hero headers and similar components:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-tsx"
      }}>{`<div style='width: 300px; margin-left: auto; margin-right: auto;'>
  <BackgroundImage
    src='https://images.unsplash.com/photo-1419242902214-272b3f66ee7a?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=720&q=80'
    radius='sm'
  >
    <Text color='#fff'>
      BackgroundImage component can be used to add any content on image. It is
      useful for hero headers and other similar sections
    </Text>
  </BackgroundImage>
</div>
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
