import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsxRuntime classic */
/* @jsx mdx */
import { LayoutDocs } from 'components/LayoutDocs';

export const meta = {
  "title": "Loader",
  "group": "svelteuidev-core",
  "packageGroup": "@svelteuidev/core",
  "category": "overlays",
  "description": "Indicate loading state",
  "example": "https://codesandbox.io/embed/loader-7run21?fontsize=14&hidenavigation=1&module=%2FApp.svelte&theme=dark&view=preview"
};

const layoutProps = {
  meta
};
const MDXLayout = ({ children, ...props }) => (
  <LayoutDocs meta={meta} {...props}>{children}</LayoutDocs>
)
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">



    <h2 {...{
      "id": "usage"
    }}>{`Usage`}<a parentName="h2" {...{
        "href": "#usage",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <p>{`By default, loader will be rendered with the SvelteUI blue color.
You can choose any color defined in the theme or extend `}<a parentName="p" {...{
        "href": "/docs/theming/create-styles"
      }}>{`your own theme`}</a>{`:`}</p>
    <h2 {...{
      "id": "configure-default-loader"
    }}>{`Configure default loader`}<a parentName="h2" {...{
        "href": "#configure-default-loader",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <p>{`You can configure default loader to be one of it's three variants`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-tsx"
      }}>{`<Loader variant="circle" />

<Loader variant="dots" />

<Loader variant="bars" />
`}</code></pre>
    <h2 {...{
      "id": "size"
    }}>{`Size`}<a parentName="h2" {...{
        "href": "#size",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <p>{`Size controls loader height or width (depends on loader variant). Loader has predefined sizes: `}<inlineCode parentName="p">{`xs`}</inlineCode>{`, `}<inlineCode parentName="p">{`sm`}</inlineCode>{`, `}<inlineCode parentName="p">{`md`}</inlineCode>{`, `}<inlineCode parentName="p">{`lg`}</inlineCode>{`, `}<inlineCode parentName="p">{`xl`}</inlineCode>{`.
Alternatively, you can use a number to set width or height in px:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-tsx"
      }}>{`<Loader size="lg" />
<Loader size={50} />
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
