import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsxRuntime classic */
/* @jsx mdx */
import { LayoutDocs } from 'components/LayoutDocs';

export const meta = {
  "title": "Switch",
  "group": "svelteuidev-core",
  "packageGroup": "@svelteuidev/core",
  "category": "inputs",
  "description": "Capture boolean input from user",
  "example": "https://codesandbox.io/embed/switch-ndbmc9?fontsize=14&hidenavigation=1&module=%2FApp.svelte&theme=dark&view=preview"
};

const layoutProps = {
  meta
};
const MDXLayout = ({ children, ...props }) => (
  <LayoutDocs meta={meta} {...props}>{children}</LayoutDocs>
)
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">



    <h2 {...{
      "id": "usage"
    }}>{`Usage`}<a parentName="h2" {...{
        "href": "#usage",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <p>{`Switch component is used to enable/disable something, normally used for boolean values or for binary actions. The component dispatches an on change event, and supports a checked prop for programmatically setting the checked state.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-tsx"
      }}>{`<Switch label="I agree to sell my privacy" size="md" color="teal"/>
<Switch onLabel="ON" offLabel="OFF" label="Setting 1" size="xl" color="pink"/>
<Switch checked size="xs"/>
`}</code></pre>
    <h2 {...{
      "id": "inner-labels"
    }}>{`Inner Labels`}<a parentName="h2" {...{
        "href": "#inner-labels",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <pre><code parentName="pre" {...{
        "className": "language-tsx"
      }}>{`<Switch size='sm' onLabel="ON" offLabel="OFF" />
<Switch size='md' onLabel="ON" offLabel="OFF" />
<Switch size='lg' onLabel="ON" offLabel="OFF" />
<Switch size='xl' onLabel="ON" offLabel="OFF" />
`}</code></pre>
    <h2 {...{
      "id": "accessibility"
    }}>{`Accessibility`}<a parentName="h2" {...{
        "href": "#accessibility",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <p>{`Switch is a regular input with checkbox type. Provide `}<inlineCode parentName="p">{`aria-label`}</inlineCode>{` if Switch is used without `}<inlineCode parentName="p">{`label`}</inlineCode>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-tsx"
      }}>{`<Switch /> // -> not ok, input is not labeled
<Switch label="I agree to everything" /> // -> ok, input and label is connected
<Switch aria-label="I agree to everything" /> // -> ok, label is not visible but will be announced by screen reader
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
