import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsxRuntime classic */
/* @jsx mdx */
import { LayoutDocs } from 'components/LayoutDocs';

export const meta = {
  "title": "ActionIcon",
  "group": "svelteuidev-core",
  "packageGroup": "@svelteuidev/core",
  "category": "inputs",
  "description": "Icon button to indicate secondary action",
  "example": "https://codesandbox.io/embed/action-icon-yx5746?fontsize=14&hidenavigation=1&module=%2FApp.svelte&theme=dark&view=preview"
};

const layoutProps = {
  meta
};
const MDXLayout = ({ children, ...props }) => (
  <LayoutDocs meta={meta} {...props}>{children}</LayoutDocs>
)
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">



    <h2 {...{
      "id": "usage"
    }}>{`Usage`}<a parentName="h2" {...{
        "href": "#usage",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <p>{`ActionIcon accepts anything as child. It `}<strong parentName="p">{`does not control icon size`}</strong>{`, specify it manually on icon component to match ActionIcon size. For example, in `}<a parentName="p" {...{
        "href": "https://www.npmjs.com/package/radix-icons-svelte"
      }}>{`radix-icons-svelte`}</a>{` you can use size prop:`}</p>
    <h2 {...{
      "id": "variants"
    }}>{`Variants`}<a parentName="h2" {...{
        "href": "#variants",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <p>{`ActionIcon has 6 variants: hover, default, transparent, filled, light and outline:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-tsx"
      }}>{`<ActionIcon color="blue" variant="transparent"><Gear size={16} /></ActionIcon>
<ActionIcon color="blue" variant="hover"><Gear size={16} /></ActionIcon>
<ActionIcon color="blue" variant="default"><Gear size={16} /></ActionIcon>
<ActionIcon color="blue" variant="outline"><Gear size={16} /></ActionIcon>
<ActionIcon color="blue" variant="filled"><Gear size={16} /></ActionIcon>
<ActionIcon color="blue" variant="light"><Gear size={16} /></ActionIcon>
`}</code></pre>
    <h2 {...{
      "id": "color"
    }}>{`Color`}<a parentName="h2" {...{
        "href": "#color",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <p>{`ActionIcon supports all colors from `}<a parentName="p" {...{
        "href": "/theming/default-theme"
      }}>{`theme.colors`}</a>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-tsx"
      }}>{`<ActionIcon color="red" />
<ActionIcon color="blue" />
`}</code></pre>
    <h2 {...{
      "id": "size-and-radius"
    }}>{`Size and radius`}<a parentName="h2" {...{
        "href": "#size-and-radius",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <p>{`Control button width and height with `}<inlineCode parentName="p">{`size`}</inlineCode>{` and border-radius with `}<inlineCode parentName="p">{`radius`}</inlineCode>{`.
Both props have predefined values: xs, sm, md, lg, xl.
Alternatively, use a number to set radius or size in px:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-tsx"
      }}>{`<ActionIcon radius="lg" /> // -> theme predefined large radius
<ActionIcon radius={10} /> // -> { borderRadius: '10px' }

<ActionIcon size="sm" /> // -> predefined small size
<ActionIcon size={50} /> // -> { width: '50px', height: '50px' }
`}</code></pre>
    <h2 {...{
      "id": "close-button"
    }}>{`Close button`}<a parentName="h2" {...{
        "href": "#close-button",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <p>{`CloseButton is a pre-made ActionIcon with close icon, it will be used in all other components: Popover, Modal, Notification, etc. Component accepts the same props as ActionIcon with additional `}<inlineCode parentName="p">{`iconSize`}</inlineCode>{` prop to control icon width and height:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-tsx"
      }}>{`<CloseButton />
<CloseButton size="xl" iconSize={20} />
`}</code></pre>
    <h2 {...{
      "id": "accessibility"
    }}>{`Accessibility`}<a parentName="h2" {...{
        "href": "#accessibility",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <p>{`ActionIcon renders a regular button element. Set `}<inlineCode parentName="p">{`title`}</inlineCode>{` or `}<inlineCode parentName="p">{`aria-label`}</inlineCode>{` props for screen reader support:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-tsx"
      }}>{`<ActionIcon /> // -> not visible to screen reader
<ActionIcon title="Settings" /> // -> ok
<ActionIcon aria-label="Settings" /> // -> ok
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
