import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsxRuntime classic */
/* @jsx mdx */
import { LayoutDocs } from 'components/LayoutDocs';

import { Alert, Text } from '@mantine/core';
import { InfoCircledIcon } from '@radix-ui/react-icons';
export const meta = {
  "group": "svelteuidev-core",
  "packageGroup": "@svelteuidev/core",
  "title": "Code",
  "slug": "/core/code/",
  "category": "typography",
  "description": "Inline or block code without syntax highlight",
  "example": "https://codesandbox.io/embed/code-r032c1?fontsize=14&hidenavigation=1&module=%2FApp.svelte&theme=dark&view=preview"
};

const layoutProps = {
  meta
};
const MDXLayout = ({ children, ...props }) => (
  <LayoutDocs meta={meta} {...props}>{children}</LayoutDocs>
)
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">




    <h2 {...{
      "id": "inline-code"
    }}>{`Inline code`}<a parentName="h2" {...{
        "href": "#inline-code",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <p>{`By default, Code component renders inline `}<inlineCode parentName="p">{`code`}</inlineCode>{` html element:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-tsx"
      }}>{`<Code>This code will be inline</Code>
`}</code></pre>
    <h2 {...{
      "id": "block-code"
    }}>{`Block code`}<a parentName="h2" {...{
        "href": "#block-code",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <p>{`To render code in `}<inlineCode parentName="p">{`pre`}</inlineCode>{` element pass `}<inlineCode parentName="p">{`block`}</inlineCode>{` prop to Code component:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-tsx"
      }}>{`<Code block copy message={codeForDemo}>
  {codeForDemo}
</Code>
`}</code></pre>
    <h2 {...{
      "id": "custom-color"
    }}>{`Custom color`}<a parentName="h2" {...{
        "href": "#custom-color",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <p>{`By default, code has gray color,
you can change it to any color from `}<a parentName="p" {...{
        "href": "/theming/extend-theme/#default-colors"
      }}>{`theme colors`}</a>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-tsx"
      }}>{`<Code color="red">This will be inline</Code>
<Code color="teal">This will be inline</Code>
<Code color="blue">This will be inline</Code>
`}</code></pre>
    <h2 {...{
      "id": "syntax-highlight"
    }}>{`Syntax highlight`}<a parentName="h2" {...{
        "href": "#syntax-highlight",
        "title": "Direct link to heading",
        "className": "anchor"
      }}><span parentName="a" {...{
          "className": "icon icon-link"
        }}></span></a></h2>
    <Alert icon={<InfoCircledIcon mdxType="InfoCircledIcon" />} title='Warning' color='yellow' mdxType="Alert">
  <Text mdxType="Text">
    In case you need syntax highlighting, a @sveltuidev/prism package will be
    available soon.
  </Text>
    </Alert>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
